<template>
	<fragment>
		<div class="logos-row">
			<div
				class="venue-logo"
				v-for="venue in venues"
				:key="venue.name"
				v-on:click="changeActiveLocation(venue.name)"
				target="blank"
			>
				<img :src="venue.imageUrl" :alt="venue.name" />
			</div>
		</div>
		<div class="map">
			<div
				v-if="Object.keys(this.activeLocation).length > 0"
				class="info-box"
			>
				<div class="info-box-header">
					<div class="info-title">{{ activeLocation.name }}</div>
					<div class="close" v-on:click="activeLocation = {}"></div>
				</div>
				<div class="info-box-container">
					<div class="address">
						<label> address </label>
						<a href="#" class="address-link">
							<span>
								{{ activeLocation.address }}
							</span>
							<br />
							<span>Denver, Colorado </span></a
						>
					</div>
					<div class="web">
						<label>web address </label>
						<a href="#" class="web-link">
							{{ activeLocation.linkUrl }}
						</a>
					</div>
				</div>
			</div>
